<script>
import { computed } from "vue";
import { useStore } from "vuex";

import MenuMainItem from "@/components/menu/items/MenuMainItem.vue";

export default {
  components: {
    MenuMainItem,
  },
  setup() {
    const store = useStore();
    const map_data = computed(() => store.state.menuStore.map_data);
    const scores = computed(() => store.state.menuStore.scores);
    const r_num = computed(() => store.state.menuStore.r_num);
    const d_type = computed(() => store.state.menuStore.d_type);
    return { store, map_data, scores, r_num, d_type };
  },
  mounted() {
    this.store.dispatch("menuStore/get_options_by_region", this.r_num);
  },
  methods: {
    onClickMap(num) {
      this.store.dispatch("menuStore/set_score", this.scores[num - 1]);
      this.store.dispatch("menuStore/set_region", this.map_data[num - 1].name);
      this.store.dispatch("menuStore/set_r_num", num - 1);
      // axios 처리 : 로컬에서는 주석처리하고 사용할 것!
      this.store.dispatch("menuStore/get_options_by_region", num - 1);
    },
  },
};
</script>

